import React from "react";

import SearchBar from "./SearchBar";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      term: "",
    };
  }

  onFormSubmit = (event, term) => {
    this.setState({ term: term });
  };

  render() {
    return (
      <div className="ui container">
        <SearchBar />
      </div>
    );
  }
}

export default App;
